function slickStuff(){$(".center").slick({dots:!0,infinite:!1,centerMode:!1,slidesToShow:3,slidesToScroll:1,autoplay:!0,autoplaySpeed:1600,responsive:[{breakpoint:1010,settings:{slidesToShow:2,infinite:!1}},{breakpoint:800,settings:{slidesToShow:2,infinite:!1}},{breakpoint:600,settings:{slidesToShow:1,centerMode:!0,dots:!0,arrows:!1}},{breakpoint:300,settings:"unslick"}]})}function readMore(){$("h4#reading").click(function(){jQuery(this).parent(".article-info").parent("section").addClass("active")}),$("h4#exit-reading").click(function(){jQuery(this).parent(".article-info").parent("section").hasClass("active")&&jQuery(this).parent(".article-info").parent("section").removeClass("active")})}$("document").ready(function(){slickStuff(),readMore()});